import './results.css';
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import { Scatter } from 'react-chartjs-2';
import 'chart.js/auto';
//import { drawQuadrantsPlugin } from './graph-shading.js'; // Adjust the path as necessary
//import { drawCenterCirclePlugin } from './center-circle.js'; // Adjust the path as necessary
import 'chartjs-plugin-annotation';
import Chart from 'chart.js/auto';

// Register custom plugins
//Chart.register(drawQuadrantsPlugin(Chart));
//Chart.register(drawCenterCirclePlugin(Chart));



function Results() {
  const [userData, setUserData] = useState(null);
  const [selectedInfo, setSelectedInfo] = useState("");
  const chartRef = useRef(null);

  const location = useLocation();

  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const email = queryParams.get('email');

    if (email) {
      axios.get(`http://localhost:3001/api/user-data?email=${email}`)
        .then(response => {
          console.log('Data received:', response.data);
          setUserData(response.data);
        })
        .catch(error => {
          console.error('Error fetching user data:', error);
        });
    } else {
      console.error('No email provided in query parameters');
    }
  }, [location]);

  const chartData = userData ? {
    datasets: [{
      label: 'Leadership Style',
      data: [{
        x: parseInt(userData.winningFocus),
        y: parseInt(userData.teamFocus)
      }],
      backgroundColor: 'rgba(255, 99, 132, 1)',
      pointRadius: 20,
      showLine: false,
    }],
  } : {};

  const medianX = 18; // Median values for x-axis
  const medianY = 18; // Median values for y-axis

  const options = {
    aspectRatio: null,
    backgroundColor: 'transparent',
    scales: {
      x: {
        type: 'linear',
        position: 'bottom',
        min: 6,
        max: 30,
        title: {
          display: true,
          text: 'Win Focus',
          color: 'black',
          font: { size: 16 },
        },
        grid: {
          display: false,
          borderColor: 'transparent',
          drawBorder: false,
        },
        ticks: {
          display: false
        }
      },
      y: {
        min: 6,
        max: 30,
        title: {
          display: true,
          text: 'Team Focus',
          color: 'black',
          font: { size: 16 }
        },
        grid: {
          display: false,
          borderColor: 'transparent',
          drawBorder: false,
        },
        ticks: {
          display: false
        }
      }
    },
    maintainAspectRatio: true,
    // plugins: {
    //   annotation: {
    //     drawTime: 'beforeDatasetsDraw',
    //     annotations: {
    //       // Quadrant and middle area annotations
    //       box1: {
    //         type: 'box',
    //         xMin: 6,
    //         xMax: medianX,
    //         yMin: medianY,
    //         yMax: 30,
    //         backgroundColor: 'rgba(255, 99, 132, 0.25)' // Red
    //       },
    //       box2: {
    //         type: 'box',
    //         xMin: medianX,
    //         xMax: 30,
    //         yMin: medianY,
    //         yMax: 30,
    //         backgroundColor: 'rgba(0, 255, 0, 0.25)' // Green
    //       },
    //       box3: {
    //         type: 'box',
    //         xMin: 6,
    //         xMax: medianX,
    //         yMin: 6,
    //         yMax: medianY,
    //         backgroundColor: 'rgba(0, 0, 255, 0.25)' // Blue
    //       },
    //       box4: {
    //         type: 'box',
    //         xMin: medianX,
    //         xMax: 30,
    //         yMin: 6,
    //         yMax: medianY,
    //         backgroundColor: 'rgba(255, 255, 0, 0.25)' // Yellow
    //       },

    //     }
    //   }
    //}
  };

  return (
    <div className="results-container">
      <div className="centered-content">
        <h1 className="results-title">Your Results</h1>
        <p className="results-description">Here are the results of your leadership test:</p>
        {userData ? (
          <>
            <div className="chart-container" id="myChart"> {/* Add an id to the chart container */}
              <Scatter ref={chartRef} data={chartData} options={options} />
            </div>
            {selectedInfo && <div className="info-display">{selectedInfo}</div>}
            <div className="leader-info">
              <h2>Leader Type: {userData.leaderType}</h2>
              <p>{userData.leaderDescription}</p>
            </div>
          </>
        ) : (
          <p>Loading results...</p>
        )}
      </div>
    </div>
  );
}

export default Results;
