import './results.css';
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import { Scatter } from 'react-chartjs-2';
import 'chart.js/auto';
import 'chartjs-plugin-annotation';
import Chart from 'chart.js/auto';

// Import the explanations object
import { explanations } from './explanations'; // Adjust the path as necessary

function Results() {
  const [userData, setUserData] = useState(null);
  const [selectedInfo, setSelectedInfo] = useState("");
  const chartRef = useRef(null);

  const location = useLocation();

  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const email = queryParams.get('email');

    if (email) {
      axios.get(`http://localhost:3001/api/user-data?email=${email}`)
        .then(response => {
          console.log('Data received:', response.data);
          setUserData(response.data);
        })
        .catch(error => {
          console.error('Error fetching user data:', error);
        });
    } else {
      console.error('No email provided in query parameters');
    }
  }, [location]);

  // Determine the selected leadership style
  const selectedLeadershipStyle = userData ? userData.leaderType : null;

  // Define a function to render the explanation based on the selected style
  const renderExplanation = () => {
    if (selectedLeadershipStyle) {
      // Access the explanation based on the selected style
      const explanation = explanations[selectedLeadershipStyle];
      if (explanation) {
        return (
          <>
            <h2>{explanation.title}</h2>
            <p>{explanation.description}</p>
            <ul>
              {explanation.advantages.map((advantage, index) => (
                <li key={index}>{advantage}</li>
              ))}
            </ul>
            <ul>
              {explanation.drawbacks.map((drawback, index) => (
                <li key={index}>{drawback}</li>
              ))}
            </ul>
            <ul>
              {explanation.potentialIssues.map((issue, index) => (
                <li key={index}>{issue}</li>
              ))}
            </ul>
          </>
        );
      }
    }

    // If no leadership style is selected, display a loading message
    return <p>Loading results...</p>;
  };

  return (
    <div className="results-container">
      <div className="centered-content">
        <h1 className="results-title">Your Results</h1>
        <p className="results-description">Here are the results of your leadership test:</p>
        {userData ? (
          <>
            <div className="chart-container" id="myChart">
              <Scatter ref={chartRef} data={chartData} options={options} />
            </div>
            {selectedInfo && <div className="info-display">{selectedInfo}</div>}
            <div className="leader-info">
              <h2>Leader Type: {userData.leaderType}</h2>
              {renderExplanation()} {/* Render the explanation */}
            </div>
          </>
        ) : (
          <p>Loading results...</p>
        )}
      </div>
    </div>
  );
}

export default Results;
